import React, {useState, useEffect} from "react";
import ProductCategories from "./ProductCategories";
import Navbar from "../Navbar";
import Footer from "../Footer";
import './BabyProduct.css';
import Slider1 from '../images/MainBanner/Category-Slideshow_1.jpg';
import Slider2 from '../images/MainBanner/Category-Slideshow_2.jpg';
import Slider3 from '../images/MainBanner/Girls_Mobile.jpg';
import Product1 from '../images/Products/Product1.png';
import Product2 from '../images/Products/Product2.png';
import Product3 from '../images/Products/Product3.png';
import Product4 from '../images/Products/Product4.png';
import Product5 from '../images/Products/Product5.png';
import Product6 from '../images/Products/Product6.png';
import Product7 from '../images/Products/Product7.png';
import Product8 from '../images/Products/Product8.png';
import Product9 from '../images/Products/Product9.png';
import Product10 from '../images/Products/Product10.png';
import Product11 from '../images/Products/Product11.png';
import Product12 from '../images/Products/Product12.png';
import Product13 from '../images/Products/Product13.png';
import Product14 from '../images/Products/Product14.png';
import Product15 from '../images/Products/Product15.png';
import Product16 from '../images/Products/Product16.png';

const BabyGirl = () => {
        const [data,setData]=useState(ProductCategories);
        const filterResult=(catItem)=>{
            const result=ProductCategories.filter((curData)=>{
                return curData.category===catItem;
            });
            setData(result);
        }


    return(
        <>
      <Navbar/><br/><br/>

      {/* carousel slider */}
<div id="carouselExampleCaptions" className="my-carousel carousel slide" data-bs-ride="carousel">
  <div className="carousel-indicators">
    <button type="button" data-bs-target="#carouselExampleCaptions" data-bs-slide-to="0" className="active" aria-current="true" aria-label="Slide 1"></button>
    <button type="button" data-bs-target="#carouselExampleCaptions" data-bs-slide-to="1" aria-label="Slide 2"></button>
    <button type="button" data-bs-target="#carouselExampleCaptions" data-bs-slide-to="2" aria-label="Slide 3"></button>
  </div>
  <div className="carousel-inner">
    <div className="carousel-item active">
      <img src={Slider1} className="d-block w-100" height="100%" alt="..."/>
      <div className="carousel-caption d-none d-md-block">
        <h5>Gentle & Safe Diapers</h5>
        <p>Gentle on baby skin U-shaped absorbent core to protect toddlers from leaks during active play moments</p>
        <div className="slider-btn">
            <button className="btn btn-1">Shop Now</button>
        </div>
      </div>
    </div>
    <div className="carousel-item">
      <img src={Slider2} className="d-block w-100" height="100%" alt="..."/>
      <div className="carousel-caption d-none d-md-block">
        <h5>Best Toys for Babies</h5>
        <p>We deals in wide range of toys, soft toys, Musical toys, Learning and educational toys </p>
        <div className="slider-btn">
            <button className="btn btn-1">Shop Now</button>
        </div>
      </div>
    </div>
    <div className="carousel-item">
      <img src={Slider3} className="d-block w-100" height="100%" alt="..."/>
      <div className="carousel-caption d-none d-md-block">
        <h5>Clothing & Shoes</h5>
        <p>Highest quality clothes and shoes for newborn, infant and toddlers at affordable price</p>
        <div className="slider-btn">
            <button className="btn btn-1">Shop Now</button>
        </div>
      </div>
    </div>
  </div>
  <button className="carousel-control-prev" type="button" data-bs-target="#carouselExampleCaptions" data-bs-slide="prev">
    <span className="carousel-control-prev-icon" aria-hidden="true"></span>
    <span className="visually-hidden">Previous</span>
  </button>
  <button className="carousel-control-next" type="button" data-bs-target="#carouselExampleCaptions" data-bs-slide="next">
    <span className="carousel-control-next-icon" aria-hidden="true"></span>
    <span className="visually-hidden">Next</span>
  </button>
</div>
{/* carousel Slide end */}



         <h3 className="main-head2">Shop by Size</h3>

        {/* Size Button starts */}
        <div className="buttons d-flex justify-content-center mb-5 pb-5">
        <button className="btn btn me-2" onClick={()=>setData(ProductCategories)}>All</button>
        <button className="btn btn me-2" onClick={()=>filterResult('0-3 Month')}>0-3 Month</button>
        <button className="btn btn me-2" onClick={()=>filterResult('3-6 Month')}>3-6 Month</button>
        <button className="btn btn me-2" onClick={()=>filterResult('6-9 Month')}>6-9 Month</button>
        <button className="btn btn me-2" onClick={()=>filterResult('9-12 Month')}>9-12 Month</button>
        </div>
        {/* Size Button ends */}
        <h3 className="main-head2">Shop by Size</h3>


        <section className="saleProduct">
            <div className="row product">
                {data.map((values)=>{
                const {id, name, price, productPicture1, productPicture2, title}=values;
                   return(
                   <>
                    <div className="col-md-3 col-sm-6" key={id}>
