import React from "react";
import "../SignUp.css";
import { Link  } from "react-router-dom";
import { Button, ButtonGroup, ButtonToolbar } from 'react-bootstrap-buttons';
import axios from "axios";
import { faHome } from "@fortawesome/free-solid-svg-icons";
import Home from "./Home";


export const Login=()=>{
const handleSubmitForm=async (data)=>{
try{
    console.log("FORM DATA", data);
    const url = `${process.env.REACT_APP_API_ORIGIN}/login`;

    await axios.post(url, {
      email:data.email,
      password:data.password,
    })
    .then((value) => {
        // localStorage.setItem('accessToken', response['accessToken']);
        // localStorage.setItem('user', JSON.stringify(response['user']));
        window.location.href = "/home";
      });
    console.log("User signed successful!!");


}catch(err){
    console.log(err);
}
}


// function Login() {
    return (
        <div className="container">
            <div className="row">
                <div className="col-sm-6">
                <img src='images/logo2.jpg' className="phone" alt="..."/>
                </div>
                <div className="col-sm-6">
                    <div className="right-column text-center">
                    <img src='images/logo1.jpg' className="signup-logo" alt="..."/>
                        <p className="info">Sign up to see photos and videos from your friends.</p>
                        {/* <button type="submit" className="btn btn-primary btn-block">
                        <img src={Google} alt="..."/>Log in with Google
                        </button> */}
                        <p className="or">OR</p>
                        <form onsubmit={handleSubmitForm}>
                            <div className="form-group">
                                <input type="text" className="form-control" placeholder="Username"/>
                            </div>
                            <div className="form-group">
                                <input type="password" className="form-control" placeholder="Password"/>
                            </div>
                            <button type="submit"  className="signup btn-primary btn-block">Login</button>
                        </form>
                    </div>
                    <div className="right-column-login text-center">
                    <a href="#">Forgot Password?</a>
                    </div>
                </div>
            </div>
        </div>
    );
}
// }
export default Login;
