import React from "react";
import { Inject, ScheduleComponent, Day, Week, WorkWeek, Month, 
  Agenda, ViewsDirective, ViewDirective, 
  TimelineViews, TimelineMonth, EventSettingsModel} from '@syncfusion/ej2-react-schedule';
import { DataManager, WebApiAdaptor } from '@syncfusion/ej2-data';
import Navbar from "../Navbar";
import Footer from "../Footer";
import '../Calendar/Calendar.css';

class Calendar extends React.Component<{}, {}> {

  remoteData = new DataManager({
    url: 'https://ej2services.syncfusion.com/production/web-services/api/Schedule',
    adaptor: new WebApiAdaptor,
    crossDomain: true
  });
  // localData: EventSettingsModel = {
  //   dataSource: [{
  //     id: 1,
  //     End: new Date(2021, 2, 22, 4, 30),
  //     Start: new Date(2021, 2, 22, 3, 0),
  //     Summary: '',
  //     IsAllDay: true,
  //     RecurrenceRule: 'FREQ=DAILY;INTERVAL=1;COUNT=10',
  //     IsBlock: true
  //   },
  //   {
  //     id: 2,
  //     End: new Date(2021, 2, 4, 8, 30),
  //     Start: new Date(2021, 2, 4, 7, 0),
  //     Summary: 'Meeting'
  //   }
  // ],
//   fields: {
//     subject: { name: 'Summary',default: 'No title is provided.' },
//     startTime: { name: 'Start' },
//     endTime: { name: 'End'}
//   }
// };


    render() {
      return (
      <>
      <Navbar/><br/><br/>
      <div className="calendar">
      <ScheduleComponent height='550px' currentView='Week' 
      selectedDate={new Date(2020, 2, 22)}
      eventSettings={{ dataSource: this.remoteData}}>
        <ViewsDirective>
        <ViewDirective option="Day" interval={1} startHour="03:00" endHour="18:00"></ViewDirective>
        <ViewDirective option="Week" interval={1}></ViewDirective>
        <ViewDirective option="Month" isSelected={true} showWeekNumber={true}
        showWeekend={false}>
        </ViewDirective>
        <ViewDirective option="TimelineDay"></ViewDirective>
        <ViewDirective option="TimelineMonth"></ViewDirective>
        </ViewsDirective>
        <Inject services={[Day, Week, WorkWeek, Month, Agenda, TimelineViews, TimelineMonth]} />
      </ScheduleComponent>
      </div>
      <Footer />
      </>
      );
   }
}

export default Calendar;
