import React from "react";
import Navbar from "../Navbar";
import Footer from "../Footer";
import './ProductDetails.css';

class ProductDetails extends React.Component {

    state = {
        products: [
            {
                "_id": "1",
                "title": "Simply One Piece Dresses / Frocks Full Sleeves Girl PINK 9-12m",
                "src": [
                    'images/Products/ProductDetails/Product 1/1.png',
                    'images/Products/ProductDetails/Product 1/2.png',
                    'images/Products/ProductDetails/Product 1/3.png'
                ],
                "descriptionTp" : "Product Description",
                "description1" : "Specification:",
                "content1"  : "Brand - Simply",
                "content2": "Type - Dungaree Style Frock With Inner Tee", 
                "content3":"Fabric - Knitted Cotton", 
                "content4": "Tee Sleeves - Full Sleeves, Tee Neck - Round Neck",
                "price" : 1800,
                "colors" : ["pink",],
                "count": 1
            }
        ],
        index:0
    };

    myRef = React.createRef();

    handleTab = index => {
        this.setState({index: index})
        const images = this.myRef.current.children;
        for(let i=0; i<images.length; i++){
            images[i].className = images[i].className.replace("active", "");
        }
        images[index].className = "active";
    };

    componentDidMount(){
        const {index} = this.state;
        this.myRef.current.children[index].className = "active";
    }

    render(){
        const {products, index} = this.state;
        return(
            <>
            <Navbar />
            <div className="ProductDetails">
              {
                  products.map(item => (
                      <div className="Details" key={item._id}>
                          <div className="big-img">
                              <img src={item.src[index]} alt=""/>
                              <button className="cart btn btn-outline-success" type="submit">Add To Cart</button>
                          </div>

                          <div className="box">
                              <div className="row">
                                  <h2>{item.title}</h2>
                                  <div className="underlineItem"></div>
                                  <span>LKR{item.price}</span>
                              </div>
                              <div className="colors">
                                  {
                                      item.colors.map((color, index) =>(
                                          <button style={{background: color}} className="btncolors" key={index}></button>
                                      ))
                                  }
                              </div>
                                  <p className="desTp">{item.descriptionTp}</p>
                                  <p className="des1">{item.description1}</p>
                                  <div className="detailsContent">
                                  <p>{item.content1}</p>
                                  <p>{item.content2}</p>
                                  <p>{item.content3}</p>
                                  <p>{item.content3}</p>
                                  </div>

                                  <div className="thumb" ref={this.myRef}>
                                      {
                                          item.src.map((img, index) =>(
                                              <img src={img} alt="" key={index}
                                              onClick={() => this.handleTab(index)}/>
                                          ))
                                      }
                                  </div>


                          </div>
                      </div>
                  ))
              }
            </div>
            <Footer />
            </>
        );
    };
}
export default ProductDetails;
