import React, { useEffect, useState } from 'react';
import '../TimerCounter/Counter.css';



const KIckCounter = () => {
  const [hour, setHour] = useState('00')
  const [minutes, setMinutes] = useState('00')
  const [second, setSecond] = useState('00')
  const [isActive, setIsActive] = useState(false)
  const [counter, setCounter] = useState(0)
  const [count, setCount] = useState(1)

  const incrementCount = () => {

    if(count<100){
      setCount(count+1)
    }
  }

  const resetTime = ()=>{
    setSecond('00')
    setMinutes('00')
    setHour(1)
    setCount(1)
    setCounter(0)
  }

  useEffect(()=>{
    let intervalId;
    if(isActive){
      intervalId=setInterval(()=>{
        const secondCounter=counter % 60
        const minuteCounter = Math.floor(counter/60)
        const hourCounter=Math.floor((counter/(1000*60*60))%24)

        const computedSecond = String(secondCounter).length === 1? `0${secondCounter}` : secondCounter
        const computedMinute = String(minuteCounter).length === 1? `0${minuteCounter}` : minuteCounter
        const computedHour = String(hourCounter).length === 1? `0${hourCounter}` : hourCounter

        if(computedSecond<=0){
          setSecond('00')
          setMinutes('00')
          setHour('00')
        }
        else{
          setSecond(computedSecond)
          setMinutes(computedMinute)
          setHour(computedHour)
        }
        setCounter(()=>counter+count)

      },500)
    }
  })


  return (
    <div className='kickCounter'>
      <div className='timer'>
        <span className='hour'>{hour}</span>
        <span>:</span>
        <span className='hour'>{minutes}</span>
        <span>:</span>
        <span className='hour'>{second}</span>
      </div>
      <div className='buttons'>
        <button className='start' onClick={()=>setIsActive(!isActive)}> { isActive ? 'Pause' : 'Start' } </button>
        <button onClick={resetTime}>Reset</button>
      </div>
      <div className='counter'>
        <button onClick={incrementCount}>+</button>
        <h1>{count}</h1>
        <button>-</button>
      </div>
    </div>
  );
}

export default KIckCounter;
