import React, { Component } from "react";
import "../Insights/Insights.css";
import { Box, Grid } from "@material-ui/core";
import Poll from "react-polls";

const pollAnswers2 = [
{
 id: 1,
 pollQuestion : "What framework do you prefer?",
 pollAnswers : [
  { option: "React", votes: 5 },
  { option: "Vue", votes: 2 },
  { option: "Angular", votes: 1 }
],
},
{
  id: 2,
 pollQuestion : "What do you prefer?",
 pollAnswers : [
  { option: "ect", votes: 100 },
  { option: "Vueee", votes: 12 },
  { option: "Angular", votes: 11 }
],
},
{
  id: 3,
pollQuestion : "What do you prefer hiiiii?",
pollAnswers : [
  { option: "ect", votes: 97},
  { option: "Vueee", votes: 12 },
  { option: "Angular", votes: 11 }
],
}
]
const pollStyles2 = {
  questionSeparator: false,
  questionSeparatorWidth: "question",
  questionBold: false,
  questionColor: "#4F70D6",
  align: "center",
  theme: "blue"
};


export default class Insights extends Component {
  state = {
    pollAnswers2: [...pollAnswers2],
  };

  handleVote = (voteAnswer, pollAnswers, pollNumber) => {
    const newPollAnswers = pollAnswers.map((answer) => {
      if (answer.option === voteAnswer) answer.votes++;
      return answer;
    });

    if (pollNumber === 1) {
      this.setState({
        pollAnswers2: newPollAnswers
      });
    } 
  };

  componentDidMount() {
    const {pollAnswers2} = this.state;
    this.autoAddVotes(pollAnswers2, 2);
  }

  autoAddVotes = (pollAnswers, pollNumber) => {
    setTimeout(() => {
      const choseAnswer = parseInt(Math.random() * 2, 10);
      this.handleVote(pollAnswers[choseAnswer].option, pollAnswers, pollNumber);
      this.autoAddVotes(pollAnswers, pollNumber);
    }, Math.random() * 5000);
  };

  render() {
    const {  pollAnswers2 } = this.state;

    return (
      <div className="poll">
        <Grid container spacing={3}>
          <main className="main-poll"> 
            <>
            {pollAnswers2.map((values)=>{
                const {id, pollQuestion, pollAnswers}=values;
                   return(

            <Grid item xs={12} key={id}>   
              <Poll
                question={pollQuestion}
                answers={pollAnswers}
                onVote={(voteAnswer) =>
                  this.handleVote(voteAnswer, pollAnswers2, 2)
                }
                customStyles={pollStyles2}
                noStorage
              />
            </Grid>
              )
              })}
            </> 
          </main>
        </Grid>
      </div>
    );
  }
}
