import React, { useEffect, useState } from 'react';
import { render } from 'react-dom';
import '../TimerCounter/Counter.css';

function Kick() {  

  const [time, setTime] = useState({ms:0, s:0, m:0, h:0});
  const [interv, setInterv] = useState();
  const [status, setStatus] = useState(0);


  const start = () => {
    run();
    setInterv(setInterval(run, 10));
  };

  var updatedMs = time.ms, updatedMs = time.ms, updatedS = time.s,  updatedM = time.m, updatedH = time.h;

  const run =() => {
    if(updatedM === 60) {
      updatedH++;
      updatedM = 0;
    }
    if(updatedS === 60) {
      updatedM++;
      updatedS = 0;
    }
    if(updatedM === 100) {
      updatedS++;
      updatedMs = 0;
    }
    updatedMs++;
    return setTime({ms:updatedMs, s:updatedS, m:updatedM, h:updatedH});
  };

  const stop = () => {
    clearInterval(interv);
    setStatus(2);
  };

  return (
    <div className='main-section'>
      <div className='clock-holder'>
        <div className='stopwatch'>
        <Display time={time} />
        <Btn status={status} stop={stop} start={start}/>
        </div>
      </div>
    </div>
  );
}

const Display =(props) =>{
  return(
    <>
 <h1>Kick Counter</h1>
 <div>
 <span>{(props.time.h >= 10)? props.time.h : "0"+ props.time.h}</span>&nbsp;&nbsp;
 <span>{(props.time.m >= 10)? props.time.m : "0"+ props.time.m}</span>&nbsp;&nbsp;
 <span>{(props.time.s>= 10)? props.time.s : "0"+ props.time.s}</span>&nbsp;&nbsp;
 <span>{(props.time.ms>= 10)? props.time.ms : "0"+ props.time.ms}</span>&nbsp;&nbsp;
 </div>
 </>
  )
};

const Btn = (props) => {
  return(
    <div>
      {(props.status === 0)?
      <button className='stopwatch-btn stopwatch-btn-gre' onClick={props.start}>Start</button> : ""
      }

      {(props.status === 1)?
      <div>
      <button className='stopwatch-btn stopwatch-btn-gre' onClick={props.stop}>Stop</button>
      <button className='stopwatch-btn stopwatch-btn-gre' onClick={props.reset}>Reset</button>
     </div> : ""
      }
      </div>
  );
}







export default Kick;
