import { List, Avatar } from 'antd';
import React, { Suspense, useRef, useState, useEffect } from 'react';
import { Canvas, extend, useFrame, useLoader, useThree } from 'react-three-fiber';
import * as THREE from 'three';
import { OrbitControls } from 'three/examples/jsm/controls/OrbitControls';
import { useSpring, a } from 'react-spring/three';

import './Week.css'
// import "antd/dist/antd.css";

const selectedHexColor = 0xffd5c3;

// Not neccesary but this provides free control on rotation, zooming, etc.
extend({ OrbitControls });

const Controls = (props) => {
 const { autoRotate } = props;
 const orbitRef = useRef();
 const { camera, gl } = useThree();

 useFrame(() => orbitRef.current.update());

 return (
   <orbitControls
     autoRotate={!!autoRotate}
     args={[camera, gl.domElement]}
     ref={orbitRef}
   />
 )
}

// Plane Component
const Plane = () => (
 <mesh rotation={[-Math.PI / 2, 0, 0]} position={[0, -0.5, 0]} receiveShadow>
   <planeBufferGeometry attach="geometry" args={[100, 100]} />
   <meshPhysicalMaterial attach="material" color="orange" />
 </mesh>
)

// Box Component
const Box = (props) => {
 const { title, icon, selectToogle } = props;

 // This reference
 const mesh = useRef()

 const [hovered, setHover] = useState(false)
 const [active, setActive] = useState(false)
 const [originalHex, setOriginalHex] = useState();

 // mesh every frame, this is outside of React without overhead
 useFrame(() => active ? mesh.current.rotation.y += 0.1 : mesh.current.rotation.y += 0.01);

 // Loading icon texture
 const texture = useLoader(THREE.TextureLoader, icon);

 // init and hover color handling
 useEffect(() => {
   !originalHex && setOriginalHex(mesh.current.material.color.getHex());
   hovered ? mesh.current.material.color.setHex(selectedHexColor) : (originalHex && mesh.current.material.color.setHex(originalHex));
 }, [hovered, active, originalHex])

 // react spring is not necesary. Using to provide a smooth animation on scaling
 const springProps = useSpring({
   scale: active ? [1.5, 1.5, 1.5] : [1, 1, 1],
 });

 return (
   <a.mesh
     {...props}
     ref={mesh}
     scale={springProps.scale}
     onClick={e => {
       setActive(!active)
       selectToogle({title, icon})
     }}
     onPointerOver={e => setHover(true)}
     onPointerOut={e => setHover(false)}
     castShadow
   >
     <boxBufferGeometry attach="geometry" args={[1, 1, 1]} />
     <meshPhysicalMaterial attach="material" map={texture} roughness={0} />
   </a.mesh>
 )
}

// React Component to list the cube selection
const CubeList = (props) => {
 const { selection } = props;

 return !!selection && (
   <div className="CubeList">
     <List
       itemLayout="horizontal"
       dataSource={selection}
       renderItem={item => (
         <List.Item>
           <List.Item.Meta
             avatar={<Avatar src={item.icon} />}
             title={<span className="text">{item.title}</span>}
           />
         </List.Item>
       )}
     />
   </div>
 )
}

// Main Component
export default () => {
 const [selection, setSelection] = useState([]);

 const selectToogle = ({title, icon}) => {
   const newSelection = [ ...selection ];
   const itemIndex = newSelection.findIndex(item => item.title === title)
   if (itemIndex > -1) {
     newSelection.splice(itemIndex, 1);
   } else {
     newSelection.push({title, icon});
   }
   setSelection(newSelection);
 }

 return (
   <>
     { !!selection.length && (<CubeList selection={selection} />) }
     <Canvas camera={{ position: [10, 10, 10]}} onCreated={({ gl }) => {
       gl.shadowMap.enabled = true;
       gl.shadowMap.type = THREE.PCFSoftShadowMap;
     }} >
       <Controls />
       <ambientLight intensity={0.5} />
       <spotLight position={[10, 10, 20]} penumbra={1} castShadow />
       <fog attach="fog" args={['black', 15, 50]} />
       <Plane />
       <Suspense fallback={null}>
         <Box position={[-9, 3, 0]} selectToogle={selectToogle} title="GraphQL" icon="/gql.png" />
         <Box position={[-6, 3, 0]} selectToogle={selectToogle} title="Ruby on Rails" icon="/rails.png" />
         <Box position={[-3, 3, 0]} selectToogle={selectToogle} title="React" icon="/react.png" />
         <Box position={[0, 3, 0]} selectToogle={selectToogle} title="Angular" icon="/angular.png" />
         <Box position={[3, 3, 0]} selectToogle={selectToogle} title="Vue" icon="/vue.png" />
         <Box position={[6, 3, 0]} selectToogle={selectToogle} title="Docker" icon="/docker.jpeg" />
         <Box position={[9, 3, 0]} selectToogle={selectToogle} title="Chicho" icon="/mario_question.png" />
       </Suspense>
     </Canvas>
   </>
 )
}
