import React, { Component } from "react";
import "../Insights/Insights.css";
import { Box, Grid } from "@material-ui/core";
import Poll from "react-polls";

// const pollQuestion1 = "Do you regularly do kegal exercises?";
// const pollAnswers1 = [
//   { option: "Yes", votes: 22 },
//   { option: "No", votes: 48 },
//   { option: "No, but I'm going to start", votes: 30 }
// ];
// const pollStyles1 = {
//   questionSeparator: true,
//   questionSeparatorWidth: "question",
//   questionBold: false,
//   questionColor: "#4F70D6",
//   align: "center",
//   theme: "purple"
// };

// const pollQuestion2 = "What framework do you prefer?";
// const pollAnswers2 = [
//   { option: "React", votes: 5 },
//   { option: "Vue", votes: 2 },
//   { option: "Angular", votes: 1 }
// ];

// const pollQuestion3 = "What framework do you prefer?";
// const pollAnswers3 = [
//   { option: "React", votes: 5 },
//   { option: "Vue", votes: 2 },
//   { option: "Angular", votes: 1 }
// ];

const pollAnswers2 = [
{
 id: 1,
 pollQuestion : "What framework do you prefer?",
 pollAnswers : [
  { option: "React", votes: 5 },
  { option: "Vue", votes: 2 },
  { option: "Angular", votes: 1 }
],
},
{
  id: 2,
 pollQuestion : "What do you prefer?",
 pollAnswers : [
  { option: "ect", votes: 100 },
  { option: "Vueee", votes: 12 },
  { option: "Angular", votes: 11 }
],
},
{
  id: 3,
pollQuestion : "What do you prefer hiiiii?",
pollAnswers : [
  { option: "ect", votes: 97},
  { option: "Vueee", votes: 12 },
  { option: "Angular", votes: 11 }
],
}
]
const pollStyles2 = {
  questionSeparator: false,
  questionSeparatorWidth: "question",
  questionBold: false,
  questionColor: "#4F70D6",
  align: "center",
  theme: "blue"
};


export default class Insights extends Component {
  state = {
    pollAnswers1: [...pollAnswers1],
    pollAnswers2: [...pollAnswers2],
    pollAnswers3: [...pollAnswers3]
  };

  handleVote = (voteAnswer, pollAnswers, pollNumber) => {
    const newPollAnswers = pollAnswers.map((answer) => {
      if (answer.option === voteAnswer) answer.votes++;
      return answer;
    });

    if (pollNumber === 1) {
      this.setState({
        pollAnswers1: newPollAnswers
      });
    } else {
      this.setState({
        pollAnswers2: newPollAnswers
        // pollAnswers3: newPollAnswers
      });
    }
  };

  componentDidMount() {
    const { pollAnswers1, pollAnswers2, pollAnswers3 } = this.state;
    this.autoAddVotes(pollAnswers1, 1);
    this.autoAddVotes(pollAnswers2, 2);
    this.autoAddVotes(pollAnswers3, 3);
  }

  autoAddVotes = (pollAnswers, pollNumber) => {
    setTimeout(() => {
      const choseAnswer = parseInt(Math.random() * 2, 10);
      this.handleVote(pollAnswers[choseAnswer].option, pollAnswers, pollNumber);
      this.autoAddVotes(pollAnswers, pollNumber);
    }, Math.random() * 5000);
  };

  render() {
    const { pollAnswers1, pollAnswers2, pollAnswers3 } = this.state;

    return (
      <div className="poll">
        <Grid container spacing={3}>
          <main className="main-poll">
            <Grid item xs={12}>
              <Poll
                question={pollQuestion1}
                answers={pollAnswers1}
                onVote={(voteAnswer) =>
                  this.handleVote(voteAnswer, pollAnswers1, 1)
                }
                customStyles={pollStyles1}
                noStorage
              />
            </Grid>
            {/* <>
            {pollAnswers2.map((values)=>{
                const {id, pollQuestion, pollAnswers}=values;
                   return( */}

            {/* <Grid item xs={12} key={id}>    */}
            <Grid item xs={12}>
              <Poll
                question={pollQuestion2}
                answers={pollAnswers2}
                onVote={(voteAnswer) =>
                  this.handleVote(voteAnswer, pollAnswers2, 2)
                }
                customStyles={pollStyles2}
                noStorage
              />
            </Grid>
               {/* )
              })}
            </> */}

            <Grid item xs={12}>
              <Box pr={2} pl={2} display="flex" flexDirection="row-reverse">
                <Poll
                  question={pollQuestion3}
                  answers={pollAnswers3}
                  onVote={(voteAnswer) =>
                    this.handleVote(voteAnswer, pollAnswers3, 3)
                  }
                  customStyles={pollStyles2}
                  noStorage
                />
              </Box>
            </Grid>
          </main>
        </Grid>
      </div>
    );
  }
}
