import React from "react";
import '../Calculator/Duedate.css'
import $ from 'jquery';
import moment from 'moment';



    function Duedate() {
        Date.getFormattedDateDiff = function(date1, date2) {
            var b = moment(date1),
                a = moment(date2),
                intervals = ['years','months','weeks','days'],
                out = [];

            for(var i=0; i<intervals.length; i++){
                var diff = a.diff(b, intervals[i]);
                b.add(diff, intervals[i]);
                out.push(diff + ' ' + intervals[i]);
            }
            return out.join(', ');
          };

          function calculateInterval() {
             var start = new Date(document.getElementById('start').value),
                 end   = new Date(document.getElementById('end').value);

             document.getElementById('out1').innerHTML 
               = 'Time elapsed between "' + start.toISOString().split('T')[0]
               + '" and "' + end.toISOString().split('T')[0] + '":<br/>' 
               + Date.getFormattedDateDiff(start, end);
          }

          var today   = new Date(),
              newYear = new Date(today.getFullYear(), 0, 1),
              y2k     = new Date(2000, 0, 1);

          document.getElementById('out2').innerHTML 
            = 'Time since New Year:<br/>' + Date.getFormattedDateDiff(newYear, today)
            + '<p>Time since Y2K:<br/>' + Date.getFormattedDateDiff(y2k, today) + '</p>';



    return (
      <><header><h1>Calculate the number of days between two dates</h1></header>

      <div class="wrapper">

      <p>Date: <input type="text" id="datepicker1" size="12" placeholder ="mm/dd/yyyy"/>&nbsp;<input type="text" id="alternate1" size="24"/></p>

      <p>Date: <input type="text" id="datepicker2" size="12" placeholder ="mm/dd/yyyy"/>&nbsp;<input type="text" id="alternate2" size="24"/></p>

      <button id="click">Calculate</button>

      <div id="output"></div>

      </div></>
    )
} 

export default Duedate;
