import React from "react";
import '../Calculator/Duedate.css'
import $ from 'jquery'; 


    function Duedate() {
        var d1;
        var d2;

        var t = new Date();
        var month = t.getMonth();
        var day = t.getDay();
        var date = t.getDate();
        var year = t.getFullYear();
        var days = ["Sunday","Monday","Tuesday","Wednesday","Thursday","Friday","Saturday"];
        var months = ["January","February","March","April","May","June","July","August","September","October","November","December"];
        var today = "0"+(month+1)+"/"+date+"/"+year;
        var dateStr = days[day]+', '+months[month]+' '+date+', '+year;
        document.getElementById("alternate1").value = dateStr;
        document.getElementById("datepicker1").setAttribute("value",today);
        var bb = today.split(' ');
        d1 = new Date(bb);


        $("#datepicker1").datepicker({
            showOtherMonths: true,
            selectOtherMonths: true,
            changeMonth: true,
            changeYear: true,
            altField: "#alternate1",
            altFormat: "DD, MM d, yy",

            onSelect: function() {
                var a = $.datepicker.formatDate("yy mm dd", $(this).datepicker("getDate"));
                var b = a.split(' ');
                d1 = new Date(b);
            } 
        });

            $("#datepicker2").datepicker({
            showOtherMonths: true,
            selectOtherMonths: true,
            changeMonth: true,
            changeYear: true,
            altField: "#alternate2",
            altFormat: "DD, MM d, yy",

            onSelect: function() {
                var c = $.datepicker.formatDate("yy mm dd", $(this).datepicker("getDate"));
                var g = c.split(' ');
                d2 = new Date(g);
            } 
        });

        $("#click").on('click',function(){
        var oneDay = 24*60*60*1000;	// hours*minutes*seconds*milliseconds
        var diffDays = (d2-d1)/oneDay;
        document.getElementById("output").innerHTML = diffDays + " days";
        });



    return (
      <><header><h1>Calculate the number of days between two dates</h1></header>

      <div class="wrapper">

      <p>Date: <input type="text" id="datepicker1" size="12" placeholder ="mm/dd/yyyy"/>&nbsp;<input type="text" id="alternate1" size="24"/></p>

      <p>Date: <input type="text" id="datepicker2" size="12" placeholder ="mm/dd/yyyy"/>&nbsp;<input type="text" id="alternate2" size="24"/></p>

      <button id="click">Calculate</button>

      <div id="output"></div>

      </div></>
    )
} 

export default Duedate;
