import React from "react";
import './ProductDetails.css';

class ProductDetails extends React.Component {

    state = {
        products: [
            {
                "_id": "1",
                "title": "Simply Dungaree Style Frock With Full Sleeves Inner Tee Floral Print",
                "src": [
                    'images/Products/ProductDetails/Product 1/1.png',
                    'images/Products/ProductDetails/Product 1/2.png',
                    'images/Products/ProductDetails/Product 1/3.png'
                ],
                "description" : "Simply One Piece Dresses / Frocks Full Sleeves Girl PINK 9-12m",
                "content": "Brand - Simply, Type - Dungaree Style Frock With Inner Tee, Fabric - Knitted Cotton, Tee Sleeves - Full Sleeves, Tee Neck - Round Neck",
                "price" : 1800,
                "colors" : ["blue", "red", "pink",],
                "count": 1
            }
        ],
        index:0
    };

    myRef = React.createRef();

    handleTab = index => {
        this.setState({index: index})
        const images = this.myRef.current.children;
        for(let i=0; i<images.length; i++){
            images[i].className = images[i].className.replace("active", "");
        }
        images[i].className = "active";
    }

    render(){
        const {products, index} = this.state;
        return(
            <div className="ProductDetails">
              {
                  products.map(item => (
                      <div className="Details" key={item._id}>
                          <div className="big-img">
                              <img src={item.src[index]} alt=""/>
                          </div>

                          <div className="box">
                              <div className="row">
                                  <h2>{item.title}</h2>
                                  <span>LKR{item.price}</span>
                              </div>
                              <div className="colors">
                                  {
                                      item.colors.map((color, index) =>(
                                          <button style={{background: color}} className="btncolors" key={index}></button>
                                      ))
                                  }
                                  <p>{item.description}</p>
                                  <p>{item.content}</p>

                                  <div className="thumb" ref={this.myRef}>
                                      {
                                          item.src.map((img, index) =>(
                                              <img src={img} alt="" key={index}
                                              onClick={() => this.handleTab(index)}/>
                                          ))
                                      }
                                  </div>
                                  <button className="cart btn btn-outline-success" type="submit">Add To Cart</button>
                              </div>
                          </div>
                      </div>
                  ))
              }
            </div>
        );
    };
}
export default ProductDetails;
