import React from "react";
import {useState ,useRef} from "react";
import "../SignUp.css";
import { Link  } from "react-router-dom";
import { Button, ButtonGroup, ButtonToolbar } from 'react-bootstrap-buttons';
import axios from "axios";
import { Snackbar} from "@material-ui/core";
import { faHome } from "@fortawesome/free-solid-svg-icons";
import Home from "./Home";
import MuiAlert from "@material-ui/lab/Alert";
import { Formik } from "formik";
import * as Yup from "yup";
import { FormHelperText, TextField } from "@material-ui/core";


export const Login=({onLogin})=>{

  const formRef = useRef();
  const [snackbarOpen, setSnackbarOpen] = useState(false);
  const [messageItem, setMessageItem] = useState<IMessageItem>({
    message: "",
    category: "",
  });
const handleSubmitForm=async (data)=>{
try{
    console.log("FORM DATA", data);
    const url = `${process.env.REACT_APP_API_ORIGIN}/login`;

    await axios.post(url, {
      email:data.email,
      password:data.password,
    })
    .then((value) => {
        // localStorage.setItem('accessToken', response['accessToken']);
        // localStorage.setItem('user', JSON.stringify(response['user']));
        window.location.href = "/home";
      });
      setSnackbarOpen(true);
    console.log("User signed successful!!");
    onLogin();


}catch(err){
    console.log(err);
}
}


// function Login() {
    return (

        <div className="container">
            <Snackbar
        open={snackbarOpen}
        autoHideDuration={6000}
        onClose={() => {
          setSnackbarOpen(false);
        }}
      ><MuiAlert
      elevation={6}
      variant="filled"
      onClose={() => {
        setSnackbarOpen(false);
      }}
      severity="success"
    >
      User signed successful!!
    </MuiAlert></Snackbar>

            <div className="row">
                <div className="col-sm-6">
                <img src='images/logo2.jpg' className="phone" alt="..."/>
                </div>
                <div className="col-sm-6">
                    <div className="right-column text-center">
                    <img src='images/logo1.jpg' className="signup-logo" alt="..."/>
                        <p className="info">Sign up to see photos and videos from your friends.</p>
                        {/* <button type="submit" className="btn btn-primary btn-block">
                        <img src={Google} alt="..."/>Log in with Google
                        </button> */}
                        <p className="or">OR</p>

                        <Formik
        initialValues={messageItem}
        validationSchema={Yup.object().shape({
          email: Yup.string().required("Email is required"),
          password: Yup.string().required("Password is required"),
        })}
        onSubmit={handleSubmitForm}
        innerRef={formRef}
      >
        {({
          handleChange,
          handleBlur,
          handleSubmit,
          values,
          touched,
          errors,
        }) => (<div>
                        <form noValidate autoComplete="off">
                        <TextField
                        className="form-group"
                        id="email"
                        label="Email"
                        variant="outlined"
                        name="email"
                        onChange={handleChange}
                        value={values.email}
                        error={Boolean(touched.message && errors.message)}
                        helperText={touched.message && errors.message}
                        onBlur={handleBlur}
                      />
                      <TextField
                        className="form-control"
                        id="password"
                        label="Password"
                        variant="outlined"
                        name="password"
                        onChange={handleChange}
                        value={values.password}
                        error={Boolean(touched.message && errors.message)}
                        helperText={touched.message && errors.message}
                        onBlur={handleBlur}
                      />
                            {/* <div className="form-group">
                                <input type="text" value={data.email} className="form-control" placeholder="Username"/>
                            </div>
                            <div className="form-group">
                                <input type="password" className="form-control" placeholder="Password"/>
                            </div> */}
                            <button type="submit"  className="signup btn-primary btn-block"  onClick={() => handleSubmit()}>Login</button>
                        </form>
                    </div>

                     )}
                   </Formik>

                    <div className="right-column-login text-center">
                    <a href="#">Forgot Password?</a>
                    </div>
                </div>
            </div>
            </div>
            </div>


    );
}

export default Login;
