import React, { useState } from 'react';
import '../TimerCounter/Counter.css';
import Navbar from '../Navbar';
import Footer from '../Footer';

class ContractionTimer extends React.Component {
  constructor(props, timer) {
		super(props);
		this.timer = timer;

		this.state = {
			hour: 0,
			minute: 0,
			second: 0,
			millisecond: 0
		};
	}

  handleStart = () => {
		this.timer = setInterval(() => {
			this.setState((prevState) => {
				return {
					millisecond: prevState.millisecond + 1
				};
			});

			if (this.state.millisecond === 100) {
				this.setState((prevState) => {
					return {
						second: prevState.second + 1,
						millisecond: 0
					};
				});
			}

			if (this.state.second === 60) {
				this.setState((prevState) => {
					return {
						minute: prevState.minute + 1,
						second: 0
					};
				});
			}
			if (this.state.minute === 60) {
				this.setState((prevState) => {
					return {
						hour: prevState.hour + 1,
						minute: 0
					};
				});
			}
		}, 10);
	};

  handleStop = () => {
		clearInterval(this.timer);
	};

	handleReset = () => {
		this.setState(() => {
			return {
				hour: 0,
				minute: 0,
				second: 0,
				millisecond: 0
			};
		});
	};

    render(){
      return (
        <>
        <Navbar /><br/><br/>
        <div className="parent">
				<div className="main">
        <Timer time={this.state} /> 
        <Action handleStart={this.handleStart} handleStop={this.handleStart} handleReset={this.handleReset} /> 
        </div>
        </div>                     
        <Footer />
        </>
        );
      }
    }

    const Timer = (props) => {
      return (
        <div className="timer">
          <div className="hour">{props.time.hour}</div>:
          <div className="min">{props.time.minute}</div>:
          <div className="sec">{props.time.second}</div>:
          <div className="msec">{props.time.millisecond}</div>
        </div>
      );
    };

    const Action = (props) => {
      return (
        <div className="action">
          <button onClick={props.handleReset} id="reset">
            RESET
          </button>
          <button onClick={props.handleStart} id="start">
            START
          </button>
          <button onClick={props.handleStop} id="stop">
            STOP
          </button>
        </div>
      );
    };




export default ContractionTimer;
