import React, { useEffect, useState } from 'react';
import { render } from 'react-dom';
import '../TimerCounter/Counter.css';

class Kick extends React.Component {  
  constructor(props, timer){  
    super(props);  
    this.timer = timer;

    this.state = {  
         hour: 0,
         minute: 0,
         second: 0,
         millisecond: 0
      };  
  }  

  handleStart = ()=>{  
   this.timer = setInterval(()=>{
     this.setState((prevState) => {
       return{
        //  second: prevState.second+1
       };
     });

     if(this.state.second === 60){
       this.setState((prevState) => {
         return{
           minute: prevState.minute + 1,
           second: 0
         };
       });
     }

     if(this.state.minute === 60) {
       this.setState((prevState) => {
         return{
           hour: prevState.hour + 1,
           minute: 0
         };
       });
     }
   }, 10);
  };

  handleStop = ()=>{  
    clearInterval(this.timer);
  }; 

  handleReset = ()=>{  
    this.setState(() => {
      return{
        hour:0,
        minute: 0,
        second: 0
      }
    }); 
  }; 


  render() {  
    return(
    <div>
      <Header />
      <Timer time={this.state}/>
      <Action handleStart={this.handleStart} handleStop={this.handleStop} handleReset={this.handleReset}/>
    </div>
    );  
  }  
}

const Header =() =>{
  return<h1>Kick Counter</h1>
};

const Timer = (props) => {
  return(
    <div>
      <div>{props.time.hour}</div>:
      <div>{props.time.minute}</div>:
      <div>{props.time.second}</div>
    </div>
  );
};

const Action = (props) =>{
  return (  
    <div>
      <button onClick={props.handleReset}>RESET</button>
      <button onClick={props.handleStart}>START</button>
      <button onClick={props.handleStop}>STOP</button>
    </div>
  );
}





export default Kick;
