import React, { Component } from 'react';
import Slider from "react-slick";
import { BsChevronLeft, BsChevronRight } from "react-icons/bs";
import "../welcome/Welcome.css";
import { Link  } from "react-router-dom";
import babyname from "../images/baby-name-finder.png";
import ovulation from "../images/ovulation-calcutor.png";
import growth from "../images/Height-and-weight-chart_3x.png.pagespeed.ce.UaksTMzqRr.png";
import duedate from "../images/duedate.png";


const NextArrow = ({ onClick }) => {
    return (
      <div className="nextArrow" onClick={onClick}>
        <BsChevronRight />
      </div>
    );
  };

  const PrevArrow = ({ onClick }) => {
    return (
      <div className="prevArrow" onClick={onClick}>
        <BsChevronLeft />
      </div>
    );
  };

  const ImageSlider = ({ images, slidesToShow = 3 }) => {
    const [imageIndex, setImageIndex] = useState(0);

    const settings = {
      className: "center",
      centerMode: true,
      infinite: true,
      dots: false,
      speed: 300,
      slidesToShow: slidesToShow,
      centerPadding: "0",
      swipeToSlide: true,
      focusOnSelect: true,
      nextArrow: <NextArrow onClick />,
      prevArrow: <PrevArrow onClick />,
      beforeChange: (current, next) => setImageIndex(next),
      responsive: [
        {
          breakpoint: 1490,
          settings: {
            slidesToShow: 3,
            slidesToScroll: 1
          }
        },
        {
          breakpoint: 820,
          settings: {
            slidesToShow: 1,
            slidesToScroll: 1
          }
        }
      ]
    };

    const templateImages = images.map((image, idx) => {
      if (image !== null) {
        return (
          <div
            className={idx === imageIndex ? "activeSlide" : "slide"}
            key={image.id}
          >
            <div className="slideWrapper">
              {image.code ? image.code : <img src={image.src} alt={image.alt} />}
            </div>
          </div>
        );
      }
      return null;
    });

    return <Slider {...settings}>{templateImages}</Slider>;
  };

  export default ImageSlider;
