import React, { Component, useState } from "react";
import "../Insights/Insights.css";
import { Box, Grid } from "@material-ui/core";
import Poll from "react-polls";
import Navbar from '../Navbar';
import Footer from '../Footer';

const pollAnswers2 = [
{
 id: 1,
 pollQuestion : "Do you work out at home?",
 pollAnswers : [
  { option: "Yes", votes: 10 },
  { option: "No", votes: 40 }
],},
{
  id: 2,
 pollQuestion : "Do you exercise to lose weight?",
 pollAnswers : [
  { option: "Yes", votes: 12 },
  { option: "No", votes: 40 }
],},
{
  id: 3,
pollQuestion : "Do you exercise to build muscle?",
pollAnswers : [
  { option: "Yes", votes: 15},
  { option: "No", votes: 45 }
],},
{
  id: 4,
pollQuestion : "Do you regularly do kegel exercise?",
pollAnswers : [
  { option: "Yes", votes: 13},
  { option: "No", votes: 26, }
],category:"By Category : Activity"},
{
  id: 5,
pollQuestion : "What do you prefer hiiiii?",
pollAnswers : [
  { option: "ect", votes: 97},
  { option: "Vueee", votes: 12 },
  { option: "Angular", votes: 11 }
],},
{
  id: 6,
pollQuestion : "What do you prefer hiiiii?",
pollAnswers : [
  { option: "ect", votes: 97},
  { option: "Vueee", votes: 12 },
  { option: "Angular", votes: 11 }
],},
{
  id: 7,
pollQuestion : "What do you prefer hiiiii?",
pollAnswers : [
  { option: "ect", votes: 97},
  { option: "Vueee", votes: 12 },
  { option: "Angular", votes: 11 }
],},
{
  id: 8,
pollQuestion : "What do you prefer hiiiii?",
pollAnswers : [
  { option: "ect", votes: 97},
  { option: "Vueee", votes: 12 },
  { option: "Angular", votes: 11 }
],},

]
const pollStyles2 = {
  questionSeparator: true,
  questionSeparatorWidth: "question",
  questionBold: false,
  questionColor: "#4F70D6",
  align: "center",
  theme: "blue",
};


export default class Insights extends Component {

  state = {
    pollAnswers2: [...pollAnswers2],
  };

  handleVote = (voteAnswer, pollAnswers, pollNumber) => {
    const newPollAnswers = pollAnswers.map((answer) => {
      if (answer.option === voteAnswer) answer.votes++;
      return answer;
    });

    if (pollNumber === 1) {
      this.setState({
        pollAnswers2: newPollAnswers
      });
    } 
  };

  componentDidMount() {
    const {pollAnswers2} = this.state;
    this.autoAddVotes(pollAnswers2, 2);
  }

  autoAddVotes = (pollAnswers, pollNumber) => {
    setTimeout(() => {
      const choseAnswer = parseInt(Math.random() * 2, 10);
      this.handleVote(pollAnswers[choseAnswer].option, pollAnswers, pollNumber);
      this.autoAddVotes(pollAnswers, pollNumber);
    }, Math.random() * 5000);
  };


  render() {

    const {  pollAnswers2 } = this.state;

    return (
    <>
      <Navbar />

      <section className="poll">
      <h3 className="main-heading center">Welcome to Our Website</h3>
                 <h5 className="sub center">Supporting Your Pregnancy Journey</h5>
      <div className="buttons d-flex justify-content-center mb-5 pb-5">
        <button className="btn btn me-2" >All</button>
        <button className="btn btn me-2" >Activity</button>
        <button className="btn btn me-2" >Medical</button>
        <button className="btn btn me-2" >Nutrition</button>
        <button className="btn btn me-2" >Lifestyle</button>
      </div>

        <div className="row pollQuestions">

        <>
        {pollAnswers2.map((values)=>{
        const {id, pollQuestion, pollAnswers, category}=values;
            return(
              <div className="col-md-3 col-sm-8" key={id}>
                <div className="poll-grid"> 

                  <Poll
                  question={pollQuestion}
                  answers={pollAnswers}
                  onVote={(voteAnswer) =>
                  this.handleVote(voteAnswer, pollAnswers2, category, 2)
                  }
                  customStyles={pollStyles2}
                  noStorage
                  /> 
                </div>
                <div className="pollCat">{category}</div>
              </div>
            )
        })}
        </>
        </div>
      </section>

      <Footer />
    </>
    );
  }
}
