import React, { useEffect, useState } from "react";
import "../Admin/AdminLogin.css";
import { Link  } from "react-router-dom";
import Logo from '../images/Admin/men1.png';
import Login from '../images/Admin/logo3.jpg';
import { login } from '../Admin/actions';
import { Form } from "react-bootstrap";
import { useDispatch, useSelector } from "react-redux";
import { Navigate } from 'react-router-dom';
import { isUserLoggedIn } from "./actions/auth.actions";

function AdminLogin() {

    const [email, setEmail] = useState('');
    const [password, setPassword] = useState('');
    const [error, setError] = useState('');
    const auth = useSelector(state => state.auth);


    const dispatch = useDispatch();

    // useEffect(() => {
    //     dispatch(isUserLoggedIn());
    // }, []);

    const userLogin = (e) => {

        e.preventDefault();


        const user = {
            email, password
        }

        dispatch(login(user));
    }

    if(auth.authenticate){
        return <Navigate to = {'/adminDashboard'} />
    }

       return (
        <div className="container">

        <div className="row">
            <div className="col-sm-6">
            <img src={Login} className="login-logo" alt="..."/>
            </div>
            <div className="col-sm-6">
                <div className="right-column text-center">
                <img src={Logo} className="signup-logo" alt="..."/>
                    <p className="info">Sign up to see photos and videos from your friends.</p>
                    {/* <button type="submit" className="btn btn-primary btn-block">
                    <img src={Google} alt="..."/>Log in with Google
                    </button> */}
                    <p className="or">OR</p>
                    <Form onSubmit={userLogin}>
                        <div className="form-group">
                            <input type="text" className="form-control" value={email} onChange={(e) => setEmail(e.target.value)} placeholder="Email"/>
                        </div>
                        <div className="form-group">
                            <input type="password" className="form-control" value={password} onChange={(e) => setPassword(e.target.value)} placeholder="Password"/>
                        </div>
                        <Link to="/adminDashboard"><button type="submit" className="signup btn-primary btn-block">Login</button></Link>
                        </Form>
                </div>
                <div className="right-column-login text-center">
                <a href="#">Forgot Password?</a>
                </div>
            </div>
        </div>

    </div>
    );
}
export default AdminLogin;
