import React, { useRef, useState} from 'react';
import "../welcome/CountDown.css";

const App = () => {
    const [timerWeeks, setTimerWeeks] = useState('00');
    const [timerDays, setTimerDays] = useState('00');
    const [timerHours, setTimerHours] = useState('00');

    let interval = useRef();

    const startTimer = () => {
        const countdownDate = new Date('December 25, 2021 00:00:00').getTime();

        interval =setInterval(() => {
            const now =new Date().getTime();
            const distance = countdownDate - now;

            const weeks = distance / (1000)
        })
    }

    return (
        <section className='timer-container'>
            <section className='timer'>
                <div>
                    <span className='mdi mdi-calendar-clock timer-icon'></span>
                    <h2 className='countdown'>Countdown Timer</h2>
                    <p>Countdown to a really special date.</p>
                </div>
                <div>
                    <section>
                        <p>87</p>
                        <p><small>Weeks</small></p>
                    </section>
                    <span>:</span>
                    <section>
                        <p>87</p>
                        <p><small>Days</small></p>
                    </section>
                    <span>:</span>
                    <section>
                        <p>87</p>
                        <p><small>Hours</small></p>
                    </section>
                </div>
            </section>

        </section>
    );
};

export default App;
